import React, { useEffect, useState } from 'react';
import {
    Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper,
    CircularProgress, Typography
} from '@mui/material';
import MenuBar from '../Menu/MenuBar';


type TravelDetail = {
    id: number;
    destination: string;
    date: string;
    purpose: string;
};
const TravelDetailsPage = () => {
    const [travelData, setTravelData] = useState<TravelDetail[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        // Simulate fetching data
        const fetchTravelDetails = async () => {
            try {
                // Simulated delay
                await new Promise(res => setTimeout(res, 1000));

                // Mock data
                const data: TravelDetail[] = [
                    { id: 1, destination: 'Paris', date: '2025-08-10', purpose: 'Business' },
                    { id: 2, destination: 'New York', date: '2025-09-15', purpose: 'Conference' },
                    { id: 3, destination: 'Tokyo', date: '2025-10-20', purpose: 'Vacation' },
                ];

                setTravelData(data);
                setLoading(false);
            } catch (err) {
                setError('Failed to load travel details');
                setLoading(false);
            }
        };

        fetchTravelDetails();
    }, []);

    if (loading) return <CircularProgress />;
    if (error) return <Typography color="error">{error}</Typography>;


    return (
           <>
         <MenuBar />
        <TableContainer component={Paper} sx={{ maxWidth: 800, margin: 'auto', mt: 4 }}>
            <Typography variant="h6" align="center" gutterBottom>
                Travel Details
            </Typography>
            <Table>
                <TableHead>
                    <TableRow>
                        <TableCell>ID</TableCell>
                        <TableCell>Destination</TableCell>
                        <TableCell>Date</TableCell>
                        <TableCell>Purpose</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {travelData.map((row) => (
                        <TableRow key={row.id}>
                            <TableCell>{row.id}</TableCell>
                            <TableCell>{row.destination}</TableCell>
                            <TableCell>{row.date}</TableCell>
                            <TableCell>{row.purpose}</TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
        </>
    )
}

export default TravelDetailsPage
