import React, { useState } from 'react';
import {BrowserRouter as Router, Navigate, Route,  Routes } from 'react-router-dom';
import LoginPage from './components/Travel/LoginPage';
import DashboardPage from './components/Login/DashboardPage';
import TravelDetailsPage from './components/Travel/TravelDetailsPage';

function App() {

  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (


//http://localhost:3000/

//http://localhost:3000/dashboard

    <Router>
      <Routes>
       {/* Root path - Login */}
        <Route
          path="/"
          element={
            isLoggedIn ? <Navigate to="/traveldetails" /> : <LoginPage onLogin={() => setIsLoggedIn(true)} />
          }
        />

        {/* Travel Details Page (protected) */}
        <Route
          path="/traveldetails"
          element={
            isLoggedIn ? <TravelDetailsPage /> : <Navigate to="/" />
          }
        />

        {/* Dashboard Page (protected) */}
        <Route
          path="/dashboard"
          element={
            isLoggedIn ? <DashboardPage /> : <Navigate to="/" />
          }
        />

        {/* Catch-all: Redirect unknown paths to login */}
        <Route path="*" element={<Navigate to="/" />} />


      </Routes>

    </Router >

  );
}

export default App;
